  #{userId,jdbcType=VARCHAR}, #{userName,jdbcType=VARCHAR},#{description,jdbcType=VARCHAR},
  #{operationType,jdbcType=VARCHAR}, #{runTime,jdbcType=BIGINT}, #{returnValue,jdbcType=VARCHAR}
  )
